'use client'
import { get, ref} from 'firebase/database';
import { useEffect,useState } from 'react';
import { db } from '../config.js';

export default function Table() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const usersRef = ref(db);
        get(usersRef).then((snapshot) => {
            if (snapshot.exists()) {
                const usersArray = Object.entries(snapshot.val()).map(([id, data]) => ({
                    id,
                    ...data,
                }));
                setUsers(usersArray);
            } else {
                console.log('No data available'); 
            }
        }).catch((error) => {
        console.error(error)
    });
}, []);

    return(
        <View className='container mx-auto'>
            <Text className='text-4x1 font-bold text-center my-10'>Fetch Data From Realtime DB</Text>
            <View className='grid grid-cols-3 gap-4'>
                {users.map((user) => (
            <View key={user.id} className='bg-gray-100 p-4 rounded-1g'>
        <Text className='text-2x1 text-gray-900'>{user.title}</Text>
        <Text className='text-gray-600'>{user.subtitle}</Text>
            </View>
            ))}
            </View>
        </View>
    )
}
