import React, { useState } from 'react'
import { View, Text, StyleSheet, TextInput, Button } from 'react-native'
import { db } from '../config'
import { ref, set } from 'firebase/database'

const AddData = () => {
  const [ variety, setVariety ] = useState('');
  const [ ripeness, setRipeness ] = useState('');

  const dataAddOn = () => {
    set(ref(db, 'posts/' + variety),{
        variety : variety,
        ripeness : ripeness,
    }),
    setVariety('')
    setRipeness('')
  }

  return (
    <View>
    <TextInput
        placeholder='Input Variety'
        valu={variety}
        onChangeText = {(text) => setVariety(text)}
        style={styles.input}
    />
    <TextInput
        placeholder='Input Ripeness(Ripe, Unripe, Overripe)'
        valu={ripeness}
        onChangeText = {(text) => setRipeness(text)}
        style={styles.input}
    />
    <Button
      title='Submit'
      onPress={dataAddOn}
    />
    </View>
  )

}

export default AddData

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'transparent',
  },
  input:{
    borderWidth: 1,
    borderColor: 'black',
    margin: 10,
    padding: 10,
    fontSize: 18,
    borderRadius: 5
  }
});
