'use client'
import React, { useEffect, useState } from 'react';
import { get, ref } from 'firebase/database';
import { db } from '../config';

export default function Table() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const usersRef = ref(db, 'users'); // Add the correct path to your database
        get(usersRef).then((snapshot) => {
            if (snapshot.exists()) {
                const usersArray = Object.entries(snapshot.val()).map(([id, data]) => ({
                    id,
                    ...data,
                }));
                setUsers(usersArray);
            } else {
                console.log('No data available');
            }
        }).catch((error) => {
            console.error(error);
        });
    }, []);

    return (
        <main className="container mx-auto">
            <h1 className="text-4xl font-bold text-center my-10">
                Fetch Data From Realtime DB
            </h1>
            <div className="grid grid-cols-3 gap-4">
                {users.map((user) => (
                    <div key={user.id} className="bg-gray-100 p-4 rounded-lg">
                        <h2 className="text-2xl text-gray-900">{user.title}</h2>
                        <p className="text-gray-600">{user.subtitle}</p>
                    </div>
                ))}
            </div>
        </main>
    );
}
