import React, { useState } from 'react';
import { View, TextInput, Button, StyleSheet } from 'react-native';
import { db } from '../config.js';
import { ref, push, set } from 'firebase/database';
import moment from 'moment';

const AddData = () => {
  const [variety, setVariety] = useState('');
  const [ripeness, setRipeness] = useState('');

  const dataAddOn = () => {
    const formattedDate = moment(new Date(date)).format('YYYY-MM-DD');
    const formattedTime = moment(new Date(time)).format('HH:mm:ss');
    const newDataRef = push(ref(db));
    set(newDataRef, {
      variety: variety,
      ripeness: ripeness,
      date: formattedDate,
      time: formattedTime,
    });
    setVariety('');
    setRipeness('');
    setDate('');
    setTime('');
  };

  return (
    <View>
      <TextInput
        placeholder='Input Variety'
        value={variety}
        onChangeText={(text) => setVariety(text)}
        style={styles.input}
      />
      <TextInput
        placeholder='Input Ripeness(Ripe, Unripe, Overripe)'
        value={ripeness}
        onChangeText={(text) => setRipeness(text)}
        style={styles.input}
      />
      <TextInput
  placeholder='Input Date (YYYY-MM-DD)'
  value={date}
  onChangeText={(text) => setDate(text)}
  style={styles.input}
/>
<TextInput
  placeholder='Input Time (HH:mm:ss)'
  value={time}
  onChangeText={(text) => setTime(text)}
  style={styles.input}
/>
      <Button title='Submit' onPress={dataAddOn} />
    </View>
  );
};

export default AddData;

const styles = StyleSheet.create({
  input: {
    borderWidth: 1,
    borderColor: 'black',
    margin: 10,
    padding: 10,
    fontSize: 18,
    borderRadius: 5,
  },
});
