// App.js

import React, { useState, useEffect } from 'react'; 
import { StyleSheet, Text, View, ImageBackground, TextInput, Button } from 'react-native';
import axios from 'axios';

const App = () => {
  const [data, setData] = useState([]);
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [email, setEmail] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:19006/api/data');
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleSubmit = async () => {
    try {
      await axios.post('http://localhost:19006/api/data', { name, age, email });
      fetchData(); // Refresh data after adding
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <ImageBackground source={require('./assets/watermelon-seamless-pattern-green-stripes-vector-30741112.jpg')} style={styles.backgroundImage}>
      <View style={styles.container}>
        <View>
          <Text style={styles.title}>Watermelon Ripeness Result</Text>
        </View>
        <View style={styles.form}>
          <TextInput
            style={styles.input}
            placeholder="Name"
            value={name}
            onChangeText={text => setName(text)}
          />
          <TextInput
            style={styles.input}
            placeholder="Age"
            value={age}
            onChangeText={text => setAge(text)}
          />
          <TextInput
            style={styles.input}
            placeholder="Email"
            value={email}
            onChangeText={text => setEmail(text)}
          />
          <Button title="Submit" onPress={handleSubmit} />
        </View>
        <View style={styles.graph}>
          <Text style={styles.subtitle}>Data from MongoDB:</Text>
          {data.map(item => (
            <Text key={item._id}>{item.name} - {item.age} - {item.email}</Text>
          ))}
        </View>
      </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'transparent',
    padding: 20,
  },
  backgroundImage: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center"
  },
  title: {
    fontSize: 30,
    fontWeight: "bold",
    color: "white",
    marginBottom: 20,
  },
  subtitle: {
    fontSize: 20,
    fontWeight: "bold",
    color: "white",
    marginBottom: 10,
  },
  form: {
    marginBottom: 20,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  graph: {
    flex: 1,
  },
});

export default App;
