import React from "react"; 
import { StyleSheet, Text, View, FlatList } from "react-native";

const data = [
  { "Count": 1, "Variety": "Crimson", "Ripeness" : "Overripe"},
  { "Count": 2, "Variety": "Crimson", "Ripeness" : "Ripe"},
  { "Count": 3, "Variety": "Sugar Baby", "Ripeness" : "Unripe"},
];

const App = () => {

  const renderItem = ({ item }) => (
    <View style={styles.row}>
      <View style={styles.cell}>
        <Text>{item.Count}</Text>
      </View>
      <View style={styles.cell}>
        <Text>{item.Variety}</Text>
      </View>
      <View style={styles.cell}>
        <Text>{item.Ripeness}</Text>
      </View>
    </View>
  )

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Watermelon Ripeness Result</Text>

      <View style={styles.header}>
        <View style={styles.heading}>
          <Text style={styles.headingText}>Count</Text>
        </View>
        <View style={styles.heading}>
          <Text style={styles.headingText}>Variety</Text>
        </View>
        <View style={styles.heading}>
          <Text style={styles.headingText}>Ripeness</Text>
        </View>
      </View>

      <FlatList
        data={data}
        keyExtractor={(item) => item.Count.toString()}
        renderItem={renderItem}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#b3ffe0',
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 10,
  },
  title: {
    fontSize: 30,
    fontWeight: "bold",
    color: "black",
    marginBottom: 20,
    marginRight: 1150,
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingVertical: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#000',
  },
  heading: {
    flex: 1,
    alignItems: 'center',
  },
  headingText: {
    fontWeight: 'bold',
  },
  row: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    borderBottomWidth: 1,
    borderBottomColor: '#000',
    paddingVertical: 10,
  },
  cell: {
    flex: 1,
    alignItems: 'center',
  },
});

export default App;
