import React from 'react'; 
import { StyleSheet, Text, View } from 'react-native';
import { PieChart } from 'react-native-chart-kit';
import TableExample from './datatable';
import RipenessGraph from './chart';

const Data = [
  ['Count', 'Variety            ', 'Ripeness             ', 'Date', 'Time'],
  ['1', 'Sugar Baby', 'Unripe', 'Mar 9, 2024', '03:28'],
  ['2', 'Sugar Baby', 'Unripe', 'Mar 9, 2024', '03:31'],
  ['3', 'Crimson', 'Ripe', 'Mar 9, 2024', '03:37'],
  ['4', 'Crimson', 'Overripe', 'Mar 9, 2024', '03:40'],
  ['5', 'Sugar Baby', 'Ripe', 'Mar 9, 2024', '03:45'],
  ['6', 'Crimson', 'Unripe', 'Mar 9, 2024', '04:13'],
  ['7', 'Crimson', 'Overripe', 'Mar 9, 2024', '04:16'],
  ['8', 'Sugar Baby', 'Ripe', 'Mar 9, 2024', '04:17'],
];


const data = [
  {
    name: 'Unripe',
    population: 37,
    color: '#33ffad', // grey
    legendFontColor: '#7F7F7F',
    legendFontSize: 20,
  },
  {
    name: 'Ripe',
    population: 37,
    color: '#00b36b', // green
    legendFontColor: '#7F7F7F',
    legendFontSize: 20,
  },
  {
    name: 'Overripe',
    population: 26,
    color: '#004d2e', // red
    legendFontColor: '#7F7F7F',
    legendFontSize: 20,
  },
];

const chartConfig = {
  backgroundColor: 'transparent',
  backgroundGradientFrom: '#eff3ff',
  backgroundGradientTo: '#efefef',
  decimalPlaces: 0, // optional, defaults to 2dp
  color: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
  style: {
    borderRadius: 16,
  },
};


const App = () => {
  return (
    <View style={styles.container}>
      <View>
        <Text style={styles.title}>Watermelon Ripeness Result</Text>
      </View>

      <View style={styles.graph}>

      <View>
      <Table borderStyle={{ borderWidth: 1, borderColor: '#00b36b' }}>
        <Row
          data={Data[0]}
          style={{ height: 50, backgroundColor: '#00ff99' }}
          textStyle={{ margin: 50 }}
        />
        <Rows data={Data.slice(1)} textStyle={{ margin: 10 }} />
      </Table>
      </View>
      <View>
      <PieChart
        data={data}
        width={500}
        height={420}
        chartConfig={chartConfig}
        accessor={'population'}
        backgroundColor={'transparent'}
        paddingLeft={'45'}
      />
      </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#ccffeb',

  },
  title: {
    fontSize: 50,
    fontWeight: "bold",
    color: "black",
    marginBottom: 20,
    marginLeft: 20
  },
  graph: {
    flex: 1,
    marginTop: 50,
    flexDirection: 'row',
    justifyContent: 'space-evenly',
  },
});

export default App;
