'use client'
import React, { useEffect, useState } from 'react';
import { get, ref } from 'firebase/database';
import { db } from '../config';
import { View, Text, StyleSheet } from 'react-native';

export default function Table() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const usersRef = ref(db); // Update 'users' to the correct path if needed
        get(usersRef).then((snapshot) => {
            if (snapshot.exists()) {
                const usersArray = Object.entries(snapshot.val()).map(([id, data]) => ({
                    id,
                    ...data,
                }));
                setUsers(usersArray);
            } else {
                console.log('No data available');
            }
        }).catch((error) => {
            console.error(error);
        });
    }, []);

    return (
        <View style={styles.container}>
            <Text style={styles.title}>Fetch Data From Realtime DB</Text>
            <View style={styles.grid}>
                {users.map((user) => (
                    <View key={user.id} style={styles.card}>
                        <Text style={styles.cardTitle}>{user.title}</Text>
                        <Text style={styles.cardSubtitle}>{user.subtitle}</Text>
                    </View>
                ))}
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        margin: 'auto',
        alignItems: 'center'
    },
    title: {
        fontSize: 32,
        fontWeight: 'bold',
        textAlign: 'center',
        marginVertical: 20,
    },
    grid: {
        flex: 1,
        flexDirection: 'row',
        flexWrap: 'wrap',
        justifyContent: 'space-between',
    },
    card: {
        backgroundColor: 'black',
        padding: 16,
        borderRadius: 8,
        margin: 8,
        width: '30%', // Adjust the width as needed
    },
    cardTitle: {
        fontSize: 24,
        color: 'black',
    },
    cardSubtitle: {
        color: 'white',
    },
});
