import React from 'react'; 
import { StyleSheet, Text, View, ImageBackground } from 'react-native';
import TableExample from './datatable';
import RipenessGraph from './chart';
import VarietyGraph from './chart2';

const App = () => {
  return (
    <ImageBackground source={require('./assets/watermelon-seamless-pattern-green-stripes-vector-30741112.jpg')} style={styles.backgroundImage}>
      <View style={styles.container}>
        <View>
          <Text style={styles.title}>Watermelon Ripeness Result</Text>
        </View>
        <View style={styles.graph}>
          <TableExample/>
          <View>
            <View style={styles.piegraph}>
              <RipenessGraph/>
              <VarietyGraph/>
            </View>
          </View>
        </View>
      </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'transparent',
  },
  backgroundImage: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center"
  },
  title: {
    fontSize: 50,
    fontWeight: "bold",
    color: "black",
    marginBottom: 20,
    marginLeft: 20
  },
  graph: {
    flex: 1,
    marginTop: 50,
    flexDirection: 'row',
    justifyContent: 'space-evenly',
    marginLeft: 10,
  },
  piegraph: {
    flex: 1,
    marginTop: 10,
    flexDirection: 'row',
    justifyContent: 'space-evenly',
  },
});

export default App;
