import React from 'react';
import { StyleSheet, Text, View, ImageBackground } from 'react-native';
import TableExample from './datatable';
import RipenessGraph from './chart';
import VarietyGraph from './chart2';
import firebase from '@react-native-firebase/app';
import firestore from '@react-native-firebase/firestore';

const firebaseConfig = {
  // Your Firebase project configuration
};

if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

const db = firestore();

const dataset = [
  ['Count', 'Variety            ', 'Ripeness             ', 'Date', 'Time'],
  ['1', 'Sugar Baby', 'Unripe', 'Mar 9, 2024', '03:28'],
  ['2', 'Sugar Baby', 'Unripe', 'Mar 9, 2024', '03:31'],
  ['3', 'Crimson', 'Ripe', 'Mar 9, 2024', '03:37'],
  ['4', 'Crimson', 'Overripe', 'Mar 9, 2024', '03:40'],
  ['5', 'Sugar Baby', 'Ripe', 'Mar 9, 2024', '03:45'],
  ['6', 'Crimson', 'Unripe', 'Mar 9, 2024', '04:13'],
  ['7', 'Crimson', 'Overripe', 'Mar 9, 2024', '04:16'],
  ['8', 'Sugar Baby', 'Ripe', 'Mar 9, 2024', '04:17'],
];

dataset.forEach(data => {
  db.collection('fruits').add(data)
    .then(docRef => {
      console.log('Document written with ID: ', docRef.id);
    })
    .catch(error => {
      console.error('Error writing document: ', error);
    });
});

const App = () => {
  return (
    <ImageBackground source={require('./assets/watermelon-seamless-pattern-green-stripes-vector-30741112.jpg')} style={styles.backgroundImage}>
      <View style={styles.container}>
        <View>
          <Text style={styles.title}>Watermelon Ripeness Result</Text>
        </View>
        <View style={styles.graph}>
          <TableExample/>
          <View>
            <View style={styles.piegraph}>
              <RipenessGraph/>
              <VarietyGraph/>
            </View>
          </View>
        </View>
      </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'transparent',
  },
  backgroundImage: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center"
  },
  title: {
    fontSize: 50,
    fontWeight: "bold",
    color: "black",
    marginBottom: 20,
    marginLeft: 20
  },
  graph: {
    flex: 1,
    marginTop: 50,
    flexDirection: 'row',
    justifyContent: 'space-evenly',
    marginLeft: 10,
  },
  piegraph: {
    flex: 1,
    marginTop: 10,
    flexDirection: 'row',
    justifyContent: 'space-evenly',
  },
});

export default App;
