import React, { useState } from 'react';
import { View, Picker, Button, StyleSheet, Text, Pressable } from 'react-native';
import { db } from '../config.js';
import { ref, push, set } from 'firebase/database';
import moment from 'moment';

const AddData = () => {
  const [variety, setVariety] = useState('');
  const [ripeness, setRipeness] = useState('');
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState({});

  const handleVarietyChange = (itemValue) => {
    setVariety(itemValue);
  };

  const handleRipenessChange = (itemValue) => {
    setRipeness(itemValue);
  };

  const validateData = () => {
    if (!variety) {
      return { error: 'Please select a variety before submitting.' };
    }
    if (!ripeness) {
      return { error: 'Please select a ripeness before submitting.' };
    }
    return { error: null };
  };

  const addDataToDatabase = () => {
    const formattedDate = moment(new Date()).format('YYYY-MM-DD / HH:mm:ss');
    const newDataRef = push(ref(db));
    set(newDataRef, {
      variety: variety,
      ripeness: ripeness,
      dateAndtime: formattedDate,
    });
    setVariety('');
    setRipeness('');
  };

  const handleSubmit = () => {
    const validationError = validateData();
    if (validationError.error) {
      // Handle error using a toast library or notification system
      console.error(validationError.error);
      return;
    }
    setLoading(true);
    addDataToDatabase();
    setLoading(false);
  };

  const storeDataForPieGraph = () => {
    const dataForPieGraph = {
      labels: ['Variety', 'Ripeness'],
      datasets: [
        {
          data: [variety, ripeness],
          backgroundColor: ['#40A578', '#9DDE8B'],
        },
      ],
    };
    setData(dataForPieGraph);
  };

  return (
    <View style={styles.container}>
      <View style={styles.format}>
        <Text style={styles.pickerTitle}> VARIETY </Text>
        <Picker
          style={styles.pickerStyles}
          selectedValue={variety}
          onValueChange={handleVarietyChange}
        >
          <Picker.Item label="Choose Variety of Watermelon" value="" />
          <Picker.Item label="Sugar Baby" value="Sugar Baby" />
          <Picker.Item label="Crimson" value="Crimson" />
          {/* Add more variety options as needed */}
        </Picker>
      </View>

      <View style={styles.format}>
        <Text style={styles.pickerTitle2}> RIPENESS </Text>
        <Picker
          style={styles.pickerStyles}
          selectedValue={ripeness}
          onValueChange={handleRipenessChange}
        >
          <Picker.Item label="Choose Ripeness of Watermelon" value="" />
          <Picker.Item label="Ripe" value="Ripe" />
          <Picker.Item label="Unripe" value="Unripe" />
          <Picker.Item label="Overripe" value="Overripe" />
        </Picker>
      </View>
      <View style={styles.buttonFormat}>
        <Pressable style={styles.button} onPress={handleSubmit}>
          <Text style={styles.submit}>Submit</Text>
        </Pressable>
      </View>
      {loading ? (
        <Text>Loading...</Text>
      ) : (
        <PieGraph data={data} />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5FCFF',
    padding: 20,
  },
  format: {
    marginBottom: 20,
  },
  pickerTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  pickerTitle2: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  pickerStyles: {
    width: '100%',
    height: 50,
    backgroundColor: '#FFF',
    borderRadius: 10,
  },
  buttonFormat: {
    marginTop: 10,
    alignItems: 'center',
  },
  button: {
    backgroundColor: '#40A578',
    padding: 10,
    borderRadius: 5,
  },
  submit: {
    color: '#FFF',
    fontSize: 18,
  },
});

export default AddData;
