import React, { useEffect, useState } from 'react';
import { View, ScrollView, StyleSheet } from 'react-native';
import { Table, Row, Rows } from 'react-native-table-component';
import firebase from 'react-native-firebase';



const TableExample = () => {
  const [data, setData] = useState([]);

  const fetchData = async () => {
    const db = firebase.database();
    const ref = db.ref('sofdes-watermelon');
    const snapshot = await ref.once('value');
    return snapshot;
  };

  const processData = (snapshot) => {
    const dataArray = [];
    snapshot.forEach((childSnapshot) => {
      const childData = childSnapshot.val();
      dataArray.push([
        childData.variety,
        childData.ripeness,
        childData.dateAndtime,
      ]);
    });
    setData(dataArray);
  };

  useEffect(() => {
    const fetchAndProcessData = async () => {
      try {
        const snapshot = await fetchData();
        processData(snapshot);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchAndProcessData();
  }, []);

  return (
    <View style={styles.container}>
      <ScrollView horizontal={true}>
        <Table borderStyle={{ borderWidth: 2, borderColor: '#c8e1ff' }}>
          <Row data={['VARIETY', 'RIPENESS', 'DATE & TIME']} style={styles.head} textStyle={styles.text} />
          <Rows data={data} style={styles.row} textStyle={styles.text} />
        </Table>
      </ScrollView>
    </View>
  );
};

export default TableExample;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#ccffeb',
    borderColor: '#00b36b',
    borderWidth: 2,
    marginBottom: 100,
  },
});
