import React from "react"; 
import { StyleSheet, Text, View, FlatList } from "react-native";


const data = [
  { "Count": 1, "Variety": "Crimson", "Ripeness" : "Overripe"},
  { "Count": 2, "Variety": "Crimson", "Ripeness" : "Ripe"},
  { "Count": 3, "Variety": "Sugar Baby", "Ripeness" : "Unripe"},
];

const App = () => {

  const renderItem = ({ item }) => (
    <View style={styles.row}>
      <Text style={styles.cell}>{item.Count}</Text>
      <Text style={styles.cell}>{item.Variety}</Text>
      <Text style={styles.cell}>{item.Ripeness}</Text>
    </View>
  )

  return (
    <View style={styles.container}>
      <View>
        <Text style={styles.title}>Watermelon Ripeness Result</Text>
      </View>

      <View style={styles.header}>
        <Text style={styles.heading}> Count </Text>
        <Text style={styles.heading}> Variety </Text>
        <Text style={styles.heading}> Ripeness </Text>
      </View>
      <FlatList
        data={data}
        keyExtractor={(item) => { item.id.toString() }}
        renderItem={renderItem}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#b3ffe0',
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 50,
    fontWeight: "bold",
    color: "black",
    marginBottom: 20,
    marginRight: 1150,
    marginVertical: -850
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    padding: 10,
  },
  heading: {
    flex: 1
  },
  row: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginVertical: 8,
    marginHorizontal: 2,
    elevation: 1,
    borderRadius: 3,
    borderColor: '#fff',
    padding: 10,
    backgroundColor: '#fff'
  },
  cell: {
    fontSize: 15,
    textAlign: 'left',
    flex: 1,
  },
});

export default App;
