import React from 'react'; 
import { StyleSheet, Text, View, ImageBackground } from 'react-native';
import { PieChart } from 'react-native-chart-kit';

const data = [
  {
    name: 'Unripe',
    population: 37,
    color: '#33ffad', // grey
    legendFontColor: 'white',
    legendFontSize: 20,
  },
  {
    name: 'Ripe',
    population: 37,
    color: '#00b36b', // green
    legendFontColor: 'white',
    legendFontSize: 20,
  },
  {
    name: 'Overripe',
    population: 26,
    color: '#004d2e', // red
    legendFontColor: 'white',
    legendFontSize: 20,
  },
];

const chartConfig = {
  backgroundColor: 'transparent',
  backgroundGradientFrom: '#eff3ff',
  backgroundGradientTo: '#efefef',
  decimalPlaces: 0, // optional, defaults to 2dp
  color: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
  style: {
    borderRadius: 16,
  },
};

const TableExample = () => (
  // Assume implementation of TableExample here
  <View>
    <Text>Table Example</Text>
  </View>
);

const VarietyGraph = () => {
  // Assume implementation of VarietyGraph here
  return (
    <View>
      <Text>Variety Graph</Text>
    </View>
  );
};

const App = () => {
  return (
    <ImageBackground source={require('./assets/watermelon-seamless-pattern-green-stripes-vector-30741112.jpg')} style={styles.backgroundImage}>
      <View style={styles.container}>
        <View>
          <Text style={styles.title}>Watermelon Ripeness Result</Text>
        </View>
        <View style={styles.graph}>
          <TableExample/>
          <View>
            <View style={styles.piegraph}>
            <View>
      <PieChart
        data={data}
        width={500}
        height={420}
        chartConfig={chartConfig}
        accessor={'population'}
        backgroundColor={'transparent'}
        paddingLeft={'45'}
        absolute // Display labels inside the pie chart
      />
    </View>
              <VarietyGraph/>
            </View>
          </View>
        </View>
      </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'transparent',
  },
  backgroundImage: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center"
  },
  title: {
    fontSize: 50,
    fontWeight: "bold",
    color: "white",
    marginBottom: 20,
    marginLeft: 20
  },
  graph: {
    flex: 1,
    marginTop: 50,
    flexDirection: 'row',
    justifyContent: 'space-evenly',
    marginLeft: 10,
  },
  piegraph: {
    flex: 1,
    marginTop: 10,
    flexDirection: 'row',
    justifyContent: 'space-evenly',
  },
});

export default App;
